jQuery(document).ready(function (e) {
  "use strict";

  $.validator.addMethod(
    "numeric",
    function (value, element) {
      return this.optional(element) || /^[0-9]+$/.test(value);
    },
    "Please enter a number."
  );

  e(function () {
    e("#contact").validate({
      rules: {
        name: { required: !0, minlength: 2 },
        surname: { required: !0, minlength: 2 },
        email: { required: !0, email: !0 },
        subject: { required: !0 },
        message: { required: !0 },
      },
      messages: {
        name: {
          required: "Please type your name",
          minlength: "Please type your name correctly",
        },
        surname: {
          required: "Please type your surname",
          minlength: "Please type your surname correctly",
        },
        email: { required: "Please type your e-mail correctly" },
        subject: {
          required: "Please type the subject",
          minlength: "what a short subject.",
        },
        message: {
          required: "Please type your message",
          minlength: "Too short",
        },
      },
      submitHandler: function (r) {
        e(r).ajaxSubmit({
          type: "POST",
          data: e(r).serialize(),
          url: "php/process.php",
          success: function () {
            e("#contact :input").attr("disabled", "disabled"),
              e("#contact").fadeTo("slow", 0.15, function () {
                e(this).find(":input").attr("disabled", "disabled"),
                  e(this).find("label").css("cursor", "default"),
                  e("#success").fadeIn();
              });
          },
          error: function () {
            e("#contact").fadeTo("slow", 0.15, function () {
              e("#error").fadeIn();
            });
          },
        });
      },
    });

    e("#application").validate({
      rules: {
        name: { required: !0, minlength: 2 },
        surname: { required: !0, minlength: 2 },
        number: { required: !0, minlength: 10, numeric: true },
        email: { required: !0, email: !0 },
        position: { required: !0 },
        message: { required: !0 },
        cv: { required: true, extension: "pdf" }
      },
      messages: {
        name: {
          required: "Please type your name",
          minlength: "Please type your name correctly",
        },
        surname: {
          required: "Please type your surname",
          minlength: "Please type your surname correctly",
        },
        number: {
          required: "Please type your number",
          numeric: "Please type a number",
          minlength: "Please type your number (09..)",
        },
        email: { required: "Please type your e-mail correctly" },
        subject: {
          required: "Please type the subject",
          minlength: "what a short subject.",
        },
        cv: {
            required: "Please upload your CV.",
            extension: "Only PDF files are allowed."
          },
        message: {
          required: "Please type your message",
          minlength: "Too short",
        },
      },
      submitHandler: function (r) {
        e(r).ajaxSubmit({
          type: "POST",
          data: e(r).serialize(),
          url: "php/process.php",
          success: function () {
            e("#application :input").attr("disabled", "disabled"),
              e("#application").fadeTo("slow", 0.15, function () {
                e(this).find(":input").attr("disabled", "disabled"),
                  e(this).find("label").css("cursor", "default"),
                  e("#success").fadeIn();
              });
          },
          error: function () {
            e("#application").fadeTo("slow", 0.15, function () {
              e("#error").fadeIn();
            });
          },
        });
      },
    });
  });
});
